void printf(char* s, char d);

int main() {
  char c = 100;
  c *= 2;
  printf("%d\n", c);

  c /= 2;
  printf("%d\n", c);

  c++;
  printf("%d\n", c);

  int a = 1;
  a += 'c';
  printf("%d\n", a);

  long s = 100;
  s *= 2;
  printf("%d\n", s);

  s /= 4;
  printf("%d\n", s);

  --s;
  printf("%d\n", s);
}
